import React from 'react';
import './quienes.css';

const QuienesSomos = () => {
  return (
    <>
      {/* Título principal */}
      <div className="text-center py-5 bg-white">
        <h1 className="display-4 font-weight-bold text-primary text-shadow">
          Eléctricos & Soluciones
        </h1>
        <p className="lead text-secondary font-weight-bold">
          Dando soluciones a los Tolimenses desde hace 5 años.
        </p>
      </div>

      {/* Misión */}
      <div className="container-fluid seccion-color py-5">
        <div className="row align-items-center justify-content-center">
          <div className="col-md-5 text-center mb-4 mb-md-0">
            <img
              src="https://img.freepik.com/foto-gratis/objetivos-exito-estrategia-negocio_1421-33.jpg"
              alt="Misión"
              className="img-fluid rounded-circle shadow-lg floating"
              style={{
                width: '230px',
                height: '230px',
                objectFit: 'cover',
                border: '5px solid white',
              }}
            />
          </div>
          <div className="col-md-6 text-white">
            <h2 className="titulo-seccion mb-3">Nuestra Misión</h2>
            <p className="texto">
              La empresa <strong>Eléctricos y Soluciones (E&amp;S)</strong> tiene como misión exclusivamente velar por el bienestar de sus clientes, dándoles a conocer la mejor variedad y soluciones eléctricas para sus proyectos.
            </p>
            <button className="btn btn-yellow mt-3">Soluciones que iluminan tu camino</button>
          </div>
        </div>
      </div>
    </>
  );
};

export default QuienesSomos;
