import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import './CategoriaProductos.css';

// Datos ficticios de productos por categoría
const productosPorCategoria = {
  iluminacion: [
    {
      id: 1,
      nombre: 'Bombilla LED 12W',
      precio: 15000,
      imagen: 'https://images.unsplash.com/photo-1558618047-3c8c76ca7d13?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=60',
      descripcion: 'Bombilla LED de alta eficiencia energética'
    },
    {
      id: 2,
      nombre: 'Lámpara de Techo',
      precio: 85000,
      imagen: 'https://images.unsplash.com/photo-1524484485831-a92ffc0de03f?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=60',
      descripcion: 'Lámpara moderna para iluminación de interiores'
    },
    {
      id: 3,
      nombre: 'Reflector LED 50W',
      precio: 120000,
      imagen: 'https://images.unsplash.com/photo-1581833971358-2c11bcc2ed3b?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=60',
      descripcion: 'Reflector LED para exteriores'
    },
    {
      id: 4,
      nombre: 'Tira LED RGB',
      precio: 45000,
      imagen: 'https://images.unsplash.com/photo-1586953208448-b95a79798f07?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=60',
      descripcion: 'Tira LED multicolor con control remoto'
    }
  ],
  cables: [
    {
      id: 5,
      nombre: 'Cable THHN 12 AWG',
      precio: 2500,
      imagen: 'https://images.unsplash.com/photo-1558618047-3c8c76ca7d13?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=60',
      descripcion: 'Cable eléctrico calibre 12 por metro'
    },
    {
      id: 6,
      nombre: 'Conector Rápido',
      precio: 800,
      imagen: 'https://images.unsplash.com/photo-1621905251918-48416bd8575a?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=60',
      descripcion: 'Conectores rápidos para empalmes'
    },
    {
      id: 7,
      nombre: 'Extensión 20m',
      precio: 35000,
      imagen: 'https://images.unsplash.com/photo-1558618047-3c8c76ca7d13?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=60',
      descripcion: 'Cable de extensión resistente'
    },
    {
      id: 8,
      nombre: 'Cinta Aislante',
      precio: 3500,
      imagen: 'https://images.unsplash.com/photo-1581833971358-2c11bcc2ed3b?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=60',
      descripcion: 'Cinta aislante eléctrica profesional'
    }
  ],
  'tuberia y accesorios': [
    {
      id: 9,
      nombre: 'Tubo Conduit 1"',
      precio: 12000,
      imagen: 'https://images.unsplash.com/photo-1558618047-3c8c76ca7d13?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=60',
      descripcion: 'Tubo conduit galvanizado 1 pulgada'
    },
    {
      id: 10,
      nombre: 'Codo 90° PVC',
      precio: 1500,
      imagen: 'https://images.unsplash.com/photo-1621905251918-48416bd8575a?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=60',
      descripcion: 'Codo de 90 grados en PVC'
    },
    {
      id: 11,
      nombre: 'Abrazadera Metal',
      precio: 800,
      imagen: 'https://images.unsplash.com/photo-1581833971358-2c11bcc2ed3b?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=60',
      descripcion: 'Abrazadera metálica para tuberías'
    },
    {
      id: 12,
      nombre: 'Copla PVC',
      precio: 1200,
      imagen: 'https://images.unsplash.com/photo-1586953208448-b95a79798f07?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=60',
      descripcion: 'Copla de conexión en PVC'
    }
  ],
  paneles: [
    {
      id: 13,
      nombre: 'Panel Eléctrico 12 Circuitos',
      precio: 150000,
      imagen: 'https://images.unsplash.com/photo-1558618047-3c8c76ca7d13?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=60',
      descripcion: 'Panel eléctrico residencial 12 circuitos'
    },
    {
      id: 14,
      nombre: 'Breaker 20A',
      precio: 18000,
      imagen: 'https://images.unsplash.com/photo-1621905251918-48416bd8575a?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=60',
      descripcion: 'Interruptor termomagnético 20 amperios'
    },
    {
      id: 15,
      nombre: 'Medidor Monofásico',
      precio: 85000,
      imagen: 'https://images.unsplash.com/photo-1581833971358-2c11bcc2ed3b?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=60',
      descripcion: 'Medidor de energía monofásico'
    },
    {
      id: 16,
      nombre: 'Tablero Distribución',
      precio: 200000,
      imagen: 'https://images.unsplash.com/photo-1586953208448-b95a79798f07?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=60',
      descripcion: 'Tablero de distribución industrial'
    }
  ]
};

const tituloCategorias = {
  iluminacion: '💡 Iluminación',
  cables: '🔌 Cables y Conectores', 
  'tuberia y accesorios': '🔧 Tubería y Accesorios',
  paneles: '⚡ Paneles Eléctricos'
};

const CategoriaProductos = () => {
  const { categoria } = useParams();
  const [productos, setProductos] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simular carga de datos
    setTimeout(() => {
      const productosCategoria = productosPorCategoria[categoria] || [];
      setProductos(productosCategoria);
      setLoading(false);
    }, 500);
  }, [categoria]);

  const formatearPrecio = (precio) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0
    }).format(precio);
  };

  if (loading) {
    return (
      <div className="container mt-5 text-center">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Cargando...</span>
        </div>
        <p className="mt-3">Cargando productos...</p>
      </div>
    );
  }

  if (productos.length === 0) {
    return (
      <div className="container mt-5 text-center">
        <h2>Categoría no encontrada</h2>
        <p>Lo sentimos, no encontramos productos para esta categoría.</p>
        <Link to="/categorias" className="btn btn-primary">
          Ver todas las categorías
        </Link>
      </div>
    );
  }

  return (
    <div className="container mt-5">
      {/* Breadcrumb */}
      <nav aria-label="breadcrumb" className="mb-4">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to="/">Inicio</Link>
          </li>
          <li className="breadcrumb-item">
            <Link to="/categorias">Categorías</Link>
          </li>
          <li className="breadcrumb-item active" aria-current="page">
            {tituloCategorias[categoria]}
          </li>
        </ol>
      </nav>

      {/* Título */}
      <div className="text-center mb-5">
        <h1 className="titulo-categoria">{tituloCategorias[categoria]}</h1>
        <p className="subtitulo-categoria">
          Encuentra los mejores productos de {categoria.replace('-', ' ')}
        </p>
      </div>

      {/* Grid de productos */}
      <div className="row">
        {productos.map((producto) => (
          <div key={producto.id} className="col-lg-3 col-md-4 col-sm-6 mb-4">
            <div className="card producto-card h-100">
              <div className="producto-imagen-container">
                <img 
                  src={producto.imagen} 
                  className="card-img-top producto-imagen" 
                  alt={producto.nombre}
                />
                <div className="producto-overlay">
                  <button className="btn btn-outline-light btn-sm">
                    Vista rápida
                  </button>
                </div>
              </div>
              <div className="card-body d-flex flex-column">
                <h5 className="card-title producto-nombre">{producto.nombre}</h5>
                <p className="card-text producto-descripcion">{producto.descripcion}</p>
                <div className="mt-auto">
                  <p className="producto-precio">{formatearPrecio(producto.precio)}</p>
                  <div className="d-flex gap-2">
                    <button className="btn btn-primary flex-fill">
                      Agregar al carrito
                    </button>
                    <button className="btn btn-outline-secondary">
                      ❤️
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Botón volver */}
      <div className="text-center mt-5 mb-5">
        <Link to="/categorias" className="btn btn-secondary btn-lg">
          ← Volver a Categorías
        </Link>
      </div>
    </div>
  );
};

export default CategoriaProductos;
