import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const location = useLocation();

  // Efecto para detectar scroll
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Cerrar menú cuando cambia la ruta
  useEffect(() => {
    setIsMenuOpen(false);
  }, [location]);

  // Función para alternar modo oscuro
  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
    document.body.classList.toggle('dark-theme', !isDarkMode);
  };

  // Función para alternar menú móvil
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  // Función para cerrar menú
  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  // Función para verificar si el enlace está activo
  const isActiveLink = (path) => {
    return location.pathname === path;
  };

  return (
    <nav className={`navbar navbar-expand-lg custom-navbar ${isScrolled ? 'scrolled' : ''}`}>
      <div className="container">
        {/* Logo y Brand */}
        <Link className="navbar-brand" to="/" onClick={closeMenu}>
          <img
            src="https://i.postimg.cc/YCZg4n8g/LOGO-ELECTRICOS-removebg-preview.png"
            alt="Eléctricos & Soluciones Logo"
            className="navbar-logo"
            loading="lazy"
          />
          <span className="brand-text">E&S</span>
        </Link>

        {/* Toggle button para móvil */}
        <button
          className={`navbar-toggler ${isMenuOpen ? 'active' : ''}`}
          type="button"
          onClick={toggleMenu}
          aria-controls="navbarNav"
          aria-expanded={isMenuOpen}
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        {/* Enlaces de navegación */}
        <div className={`collapse navbar-collapse ${isMenuOpen ? 'show' : ''}`} id="navbarNav">
          <ul className="navbar-nav mx-auto">
            <li className="nav-item">
              <Link 
                className={`nav-link ${isActiveLink('/quienes-somos') ? 'active' : ''}`} 
                to="/quienes-somos" 
                onClick={closeMenu}
              >
                Quiénes Somos
              </Link>
            </li>
            <li className="nav-item dropdown">
              <Link 
                className={`nav-link dropdown-toggle ${isActiveLink('/categorias') ? 'active' : ''}`} 
                to="/categorias"
                onClick={closeMenu}
                role="button"
                aria-expanded="false"
              >
                Categorías
              </Link>
              {/* Dropdown menu opcional */}
              <ul className="dropdown-menu">
                <li>
                  <Link className="dropdown-item" to="/categorias/iluminacion" onClick={closeMenu}>
                    💡 Iluminación
                  </Link>
                </li>
                <li>
                  <Link className="dropdown-item" to="/categorias/cables" onClick={closeMenu}>
                    🔌 Cables y Conectores
                  </Link>
                </li>
                <li>
                  <Link className="dropdown-item" to="/categorias/herramientas" onClick={closeMenu}>
                    🔧 Herramientas
                  </Link>
                </li>
                <li>
                  <Link className="dropdown-item" to="/categorias/paneles" onClick={closeMenu}>
                    ⚡ Paneles Eléctricos
                  </Link>
                </li>
              </ul>
            </li>
            <li className="nav-item">
              <Link 
                className={`nav-link ${isActiveLink('/novedades') ? 'active' : ''}`} 
                to="/novedades" 
                onClick={closeMenu}
              >
                Novedades
              </Link>
            </li>
            <li className="nav-item">
              <Link 
                className={`nav-link ${isActiveLink('/contacto') ? 'active' : ''}`} 
                to="/contacto" 
                onClick={closeMenu}
              >
                Contacto
              </Link>
            </li>
          </ul>
                   
          {/* Iconos de la derecha */}
          <div className="navbar-icons">
            <Link 
              to="/carrito" 
              className={`nav-icon ${isActiveLink('/carrito') ? 'active' : ''}`}
              onClick={closeMenu} 
              title="Carrito de compras"
            >
              <span className="icon-cart">🛒</span>
              {/* Badge opcional para mostrar cantidad de items */}
              {/* <span className="badge">3</span> */}
            </Link>
            
            <Link 
              to="/perfil" 
              className={`nav-icon profile-icon ${isActiveLink('/perfil') ? 'active' : ''}`}
              onClick={closeMenu} 
              title="Mi perfil"
            >
              <span className="icon-profile">👤</span>
            </Link>
            
            <button 
              className="mode-toggle" 
              onClick={toggleDarkMode}
              title={isDarkMode ? "Cambiar a modo claro" : "Cambiar a modo oscuro"}
              aria-label={isDarkMode ? "Activar modo claro" : "Activar modo oscuro"}
            >
              <span className="mode-icon">
                {isDarkMode ? '☀️' : '🌙'}
              </span>
              <span className="mode-text">
                {isDarkMode ? 'Modo Claro' : 'Modo Oscuro'}
              </span>
            </button>
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
