import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const [isDarkMode, setIsDarkMode] = useState(true); // Iniciamos en modo oscuro
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [showDropdown, setShowDropdown] = useState(false);
  const location = useLocation();

  // Cerrar men√∫ cuando cambia la ruta
  useEffect(() => {
    setIsMenuOpen(false);
    setShowDropdown(false);
  }, [location]);

  // Funci√≥n para alternar modo oscuro
  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
    document.body.classList.toggle('light-theme', !isDarkMode);
  };

  // Funci√≥n para alternar men√∫ m√≥vil
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  // Funci√≥n para cerrar men√∫
  const closeMenu = () => {
    setIsMenuOpen(false);
    setShowDropdown(false);
  };

  // Funci√≥n para manejar dropdown
  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  // Funci√≥n para verificar si el enlace est√° activo
  const isActiveLink = (path) => {
    return location.pathname === path;
  };

  return (
    <nav className="custom-navbar">
      <div className="navbar-container">
        {/* Logo */}
        <Link className="navbar-logo" to="/" onClick={closeMenu}>
          <img 
  src="https://i.postimg.cc/YCZg4n8g/LOGO-ELECTRICOS-removebg-preview.png" 
  alt="Logo" 
/>
        </Link>

        {/* Toggle button para m√≥vil */}
        <button
          className={`mobile-toggle ${isMenuOpen ? 'active' : ''}`}
          onClick={toggleMenu}
          aria-label="Toggle navigation"
        >
          <span></span>
          <span></span>
          <span></span>
        </button>

        {/* Enlaces de navegaci√≥n centrales */}
        <div className={`nav-menu ${isMenuOpen ? 'active' : ''}`}>
          <Link 
            className={`nav-link ${isActiveLink('/quienes-somos') ? 'active' : ''}`} 
            to="/quienes-somos" 
            onClick={closeMenu}
          >
            Quienes Somos
          </Link>

          <div className="nav-dropdown">
            <button 
              className={`nav-link dropdown-btn ${isActiveLink('/categorias') ? 'active' : ''}`}
              onClick={toggleDropdown}
            >
              Categor√≠as
              <span className={`dropdown-arrow ${showDropdown ? 'active' : ''}`}>‚ñº</span>
            </button>
            {showDropdown && (
              <div className="dropdown-content">
                <Link to="/categorias/iluminacion" onClick={closeMenu}>üí° Iluminaci√≥n</Link>
                <Link to="/categorias/cables" onClick={closeMenu}>üîå Cables y Conectores</Link>
                <Link to="/categorias/herramientas" onClick={closeMenu}>üîß Herramientas</Link>
                <Link to="/categorias/paneles" onClick={closeMenu}>‚ö° Paneles El√©ctricos</Link>
              </div>
            )}
          </div>

          <Link 
            className={`nav-link ${isActiveLink('/novedades') ? 'active' : ''}`} 
            to="/novedades" 
            onClick={closeMenu}
          >
            Novedades
          </Link>

          <Link 
            className={`nav-link ${isActiveLink('/contacto') ? 'active' : ''}`} 
            to="/contacto" 
            onClick={closeMenu}
          >
            Contacto
          </Link>
        </div>

        {/* Iconos de la derecha */}
        <div className="nav-actions">
          <Link 
            to="/Productos" 
            className={`action-icon cart-icon ${isActiveLink('/productos') ? 'active' : ''}`}
            onClick={closeMenu}
            title="Productos"
          >
            üõí
          </Link>

          <Link 
            to="/perfil" 
            className={`action-icon profile-icon ${isActiveLink('/perfil') ? 'active' : ''}`}
            onClick={closeMenu}
            title="Perfil"
          >
            <div className="profile-circle">
              üë§
            </div>
          </Link>

          <button 
            className="mode-toggle-btn"
            onClick={toggleDarkMode}
            title={isDarkMode ? "Cambiar a modo claro" : "Cambiar a modo oscuro"}
          >
            <span className="mode-icon">{isDarkMode ? '‚òÄÔ∏è' : 'üåô'}</span>
            <span className="mode-text">Modo {isDarkMode ? 'Claro' : 'Oscuro'}</span>
          </button>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
