import React, { useEffect, useState } from 'react';

// Componente ProductoCard simulado
const ProductoCard = ({ producto }) => (
  <div className="card h-100 shadow-sm">
    <img 
      src={producto.imagen || 'https://via.placeholder.com/300x200?text=Producto'} 
      className="card-img-top" 
      alt={producto.nombre}
      style={{ height: '200px', objectFit: 'cover' }}
    />
    <div className="card-body">
      <h5 className="card-title">{producto.nombre}</h5>
      <p className="card-text">{producto.descripcion}</p>
      <p className="card-text"><strong>${producto.precio}</strong></p>
    </div>
  </div>
);

function Home() {
  const [productos, setProductos] = useState([
    { id: 1, nombre: 'Cable Eléctrico', descripcion: 'Cable de alta calidad', precio: '25000', imagen: 'https://images.unsplash.com/photo-1558618666-fcd25c85cd64?w=300&h=200&fit=crop' },
    { id: 2, nombre: 'Interruptor', descripcion: 'Interruptor de pared', precio: '15000', imagen: 'https://images.unsplash.com/photo-1621905251918-48416bd8575a?w=300&h=200&fit=crop' },
    { id: 3, nombre: 'Bombilla LED', descripción: 'Bombilla de bajo consumo', precio: '12000', imagen: 'https://images.unsplash.com/photo-1524484485831-a92ffc0de03f?w=300&h=200&fit=crop' }
  ]);

  useEffect(() => {
    // Simulando la carga de productos desde el backend
    // axios.get('http://localhost:8000/api/productos/')
    //   .then(res => setProductos(res.data))
    //   .catch(err => console.log("Error al cargar productos:", err));
  }, []);

  return (
    <div>
      <style jsx>{`
        /* Estilos para la sección hero eléctrica */
        .hero-electric {
          position: relative;
          height: 480px;
          background: linear-gradient(to top, #0a1f2f 0%, #1f6ba3 70%, #a0d8f7 100%);
          display: flex;
          align-items: center;
          justify-content: center;
          color: white;
          text-align: center;
          overflow: hidden;
          padding: 0 20px;
          box-shadow: inset 0 0 150px #135;
          font-family: 'Segoe UI', sans-serif;
        }

        .hero-text {
          position: relative;
          z-index: 10;
          max-width: 700px;
          font-size: 2rem;
          font-weight: 700;
        }

        .hero-electric svg {
          position: absolute;
          bottom: 0;
          width: 100%;
          height: 400px;
          pointer-events: none;
        }

        .ground {
          fill: #142c44;
        }

        .tower, .tower-line {
          stroke: #cbd6e1;
          stroke-width: 3;
          fill: none;
          filter: drop-shadow(1px 1px 2px rgba(0,0,0,0.4));
        }

        .tower-base {
          stroke: #9fb8c9;
          stroke-width: 2;
          fill: #b2c6db;
          rx: 5;
        }

        .power-line {
          stroke: #00ffff;
          stroke-width: 4;
          stroke-linecap: round;
          stroke-dasharray: 40 20;
          animation: dashmove 2.5s linear infinite;
          filter: drop-shadow(0 0 10px #00ffff);
        }

        .spark {
          stroke: #0ff;
          stroke-width: 5;
          stroke-linecap: round;
          stroke-dasharray: 5 5;
          animation: sparkFlash 0.5s ease-in-out infinite alternate;
          filter: drop-shadow(0 0 10px #0ff);
        }

        @keyframes dashmove {
          to {
            stroke-dashoffset: -60;
          }
        }

        @keyframes sparkFlash {
          0% {
            stroke-opacity: 0.5;
          }
          100% {
            stroke-opacity: 1;
          }
        }

        /* Productos */
        .container {
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 20px;
          box-sizing: border-box;
        }

        .py-5 {
          padding-top: 3rem;
          padding-bottom: 3rem;
        }

        .mb-4 {
          margin-bottom: 1.5rem;
        }

        .text-center {
          text-align: center;
        }

        .row {
          display: flex;
          flex-wrap: wrap;
          margin: 0 -12px;
        }

        .col-md-4 {
          flex: 0 0 33.333333%;
          max-width: 33.333333%;
          padding: 0 12px;
          margin-bottom: 1.5rem;
        }

