          .col-md-4 {
            flex: 0 0 100%;
            max-width: 100%;
          }
        }

        /* Envíos */
        .envios-section .envio-bg {
          position: relative;
          height: 320px;
          background-image: url('https://img.freepik.com/vector-premium/paquetes-entrega-mensajeria-lindo-dibujos-animados-carro-camion_338371-95.jpg?w=740');
          background-size: cover;
          background-position: center;
        }

        .envio-overlay {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background-color: rgba(0, 0, 0, 0.5);
