        .envio-text {
          position: relative;
          z-index: 2;
          padding: 0 3rem;
        }

        .envio-text h2 {
          text-shadow: 2px 2px 6px rgba(0, 0, 0, 0.7);
          font-size: 2.5rem;
          font-weight: 700;
          margin-bottom: 1.5rem;
        }

        .container-fluid {
          padding: 0;
        }

        .g-0 {
          margin: 0;
        }

        .align-items-center {
          align-items: center;
        }

        .text-white {
          color: white;
        }

        .display-5 {
          font-size: 2.5rem;
        }

        .fw-bold {
          font-weight: 700;
        }

        .btn {
          display: inline-block;
          padding: 0.75rem 1.5rem;
          margin-bottom: 0;
          font-size: 1rem;
          font-weight: 400;
          line-height: 1.5;
          text-align: center;
          text-decoration: none;
          vertical-align: middle;
          cursor: pointer;
          border: 1px solid transparent;
          border-radius: 0.375rem;
          transition: all 0.15s ease-in-out;
        }

        .btn-warning {
          color: #000;
          background-color: #ffc107;
          border-color: #ffc107;
        }

        .btn-warning:hover {
          background-color: #ffca2c;
          border-color: #ffc720;
        }

        .btn-lg {
          padding: 0.875rem 1.75rem;
          font-size: 1.125rem;
          border-radius: 0.5rem;
        }

        .shadow {
          box-shadow: 0 0.5rem 1rem rgba(0, 0, 0, 0.15);
        }

        .card {
          position: relative;
          display: flex;
          flex-direction: column;
          min-width: 0;
          word-wrap: break-word;
          background-color: #fff;
          background-clip: border-box;
          border: 1px solid rgba(0, 0, 0, 0.125);
          border-radius: 0.375rem;
        }

        .card-img-top {
          width: 100%;
          border-top-left-radius: calc(0.375rem - 1px);
          border-top-right-radius: calc(0.375rem - 1px);
        }

        .card-body {
          flex: 1 1 auto;
          padding: 1rem;
        }

        .card-title {
          margin-bottom: 0.5rem;
          font-size: 1.25rem;
          font-weight: 500;
        }

        .card-text {
          margin-bottom: 1rem;
        }

        .h-100 {
          height: 100%;
        }

        .shadow-sm {
          box-shadow: 0 0.125rem 0.25rem rgba(0, 0, 0, 0.075);
        }

        @media (max-width: 768px) {
          .hero-text {
            font-size: 1.5rem;
          }
          
          .envio-text {
            padding: 2rem 1.5rem;
