import React, { useEffect, useState } from 'react';
import emailjs from 'emailjs-com';
import './Contacto.css';

function Contacto() {
  const [formData, setFormData] = useState({
    nombre: '',
    correo: '',
    telefono: '',
    mensaje: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);

  useEffect(() => {
    // Inicializar EmailJS
    emailjs.init('Idw8E79raVsv7No_V');

    // Cargar datos guardados del localStorage
    const savedData = {
      nombre: localStorage.getItem('nombre') || '',
      correo: localStorage.getItem('correo') || '',
      telefono: localStorage.getItem('telefono') || '',
      mensaje: localStorage.getItem('mensaje') || ''
    };
    setFormData(savedData);

    // Mostrar formulario con efecto fade
    const timer = setTimeout(() => {
      const formulario = document.getElementById('formulario');
      if (formulario) {
        formulario.style.opacity = '1';
      }
    }, 100);

    return () => clearTimeout(timer);
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Guardar en localStorage
    localStorage.setItem(name, value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      await emailjs.sendForm('service_emj4bpq', 'template_rdu7hnt', e.target);
      
      // Limpiar formulario y localStorage
      setFormData({
        nombre: '',
        correo: '',
        telefono: '',
        mensaje: ''
      });
      
      ['nombre', 'correo', 'telefono', 'mensaje'].forEach(key => {
        localStorage.removeItem(key);
      });

      setShowSuccess(true);
      setTimeout(() => setShowSuccess(false), 5000);
      
    } catch (error) {
      console.error('Error al enviar:', error);
      alert('Error al enviar el mensaje. Por favor, intenta nuevamente.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="container-fluid form-section">
      <div className="container">
        <div className="row">
          <div className="titulo col-12 text-center mb-4">
            <h3 id="titulo" className="font-weight-bold">
              ¿Quieres comunicarte con nosotros?
            </h3>
          </div>

          <div id="formulario" className="col-12 col-md-10 col-lg-8 mx-auto">
            <form id="contact-form" onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="nombre">👤 Nombre Completo*</label>
                <input 
                  type="text" 
                  className="form-control" 
                  id="nombre" 
                  name="nombre" 
                  value={formData.nombre}
                  onChange={handleInputChange}
                  placeholder="Escribe tu nombre completo" 
                  autoComplete="name" 
                  required 
                />
              </div>

              <div className="form-group">
                <label htmlFor="correo">✉️ Correo Electrónico*</label>
                <input 
                  type="email" 
                  className="form-control" 
                  id="correo" 
                  name="correo" 
                  value={formData.correo}
                  onChange={handleInputChange}
                  placeholder="correo@ejemplo.com" 
                  autoComplete="email" 
                  required 
                />
              </div>

              <div className="form-group">
                <label htmlFor="telefono">📱 Número de Teléfono*</label>
                <input 
                  type="tel" 
                  className="form-control" 
                  id="telefono" 
                  name="telefono" 
                  value={formData.telefono}
                  onChange={handleInputChange}
                  placeholder="Ej: 3001234567" 
                  pattern="[0-9]{10}" 
                  title="Ingresa un número de 10 dígitos" 
                  autoComplete="tel" 
                  required 
                />
              </div>

              <div className="form-group">
                <label htmlFor="mensaje">📝 Describe por qué te comunicas con nosotros:</label>
                <textarea 
                  className="form-control" 
                  id="mensaje" 
                  name="mensaje" 
                  rows="4" 
                  value={formData.mensaje}
                  onChange={handleInputChange}
                  placeholder="Cuéntanos en qué podemos ayudarte..."
                ></textarea>
              </div>

              <div className="text-center">
                <button 
                  type="submit" 
                  className="btn btn-light"
                  disabled={isSubmitting}
                >
                  {isSubmitting ? 'Enviando...' : 'Enviar Mensaje'}
                </button>
              </div>
            </form>

            {showSuccess && (
              <p id="mensajeEnviado" style={{ display: 'block' }}>
                ¡Mensaje enviado con éxito! Te contactaremos pronto.
              </p>
            )}

            <p className="mt-3 small text-center">
              Recuerda que para evitar este proceso del registro de datos puedes optar por{' '}
              <a href="/login" className="font-weight-bold">
                Iniciar Sesión
              </a>
            </p>
          </div>

          <a 
            href="https://wa.me/573156417689?text=Hola,%20tengo%20una%20consulta" 
            target="_blank" 
            rel="noopener noreferrer"
            aria-label="Contactar por WhatsApp"
          >
            <img 
              src="https://cdn-icons-png.flaticon.com/512/220/220236.png" 
              alt="WhatsApp" 
            />
          </a>
        </div>
      </div>
    </div>
  );
}

export default Contacto;
